import React, {useState} from 'react';
import Button from './Button.jsx';
 
function ItemBuy(props){
    
    const [quant, updateQuant] = useState();
    var val = 0;

    function updateCart(){
        val = document.getElementById("quantity").value;
        //update the values in the cart
        //take value from id="quantity"
        updateQuant(val);
    }


    return(
        <div>
        <h1>{quant}</h1>        
    <div className = "singleItem">
        <img className = "itemImg"  src = {props.img} width= "200" height = "200"/>
        <h3 className = "itemTitle">{props.title}</h3>
        <h4 className = "itemPrice">{props.price}</h4>
        <input type="number" value = {quant} placeholder = "0" id="quantity" name="quantity" min="1"></input>
        <Button onClick = {updateCart} type = "button" cName = "addToCart" buttonText = "Add to Cart"/>
    </div>
    </div>);
}

export default ItemBuy;
