import React, {useState} from 'react';
import Sweet7 from '../images/sweet7.jpg';
import Sweet8 from '../images/sweet8.jpg';
import Button from './Button.jsx';



function Hero(){

    var id = 0;

    const [banner, updateBanner] = useState(Sweet7);


    function changeHero(){
        setInterval(function(){ 
            if(id === 0){
                updateBanner(Sweet7);
                id = 1;
                return banner;
            }
            if(id === 1){
                updateBanner(Sweet8);
                id = 0;
                return banner;
            }
        }, 3000);
        

        
    }


    return (
        <div className = "heroContent">
            <div>
                <h1 className = "heroText">Taste the goodness at Laddoos.</h1>
                <Button cName = "heroButton" buttonText = "Explore Menu"/>
            </div>
            <img src = {changeHero()} width = "100%" height = "100%"/>
        </div>
    );
}

export default Hero;
