import React from 'react';
import Navbar from './Navbar.jsx';
import Hero from './Hero.jsx';
import Intro from './Intro.jsx';
import Categories from './Categories.jsx';
import Decoration from './Decoration.jsx';
import BestSellers from './BestSellers.jsx';
import Reviews from './Reviews.jsx';
import Offers from './Offers.jsx';
import Footer from './Footer.jsx';
import ReviewsContent from '../reviews.js'; 

function App(){

    var id = 0;
    const [reviewItem, updateReviewItem] = [ReviewsContent[id]];

    function updateImage(){
        var interval = setInterval(function(){ 
            updateReviewItem(ReviewsContent[id++]); 
        }, 3000);
        if(id == ReviewsContent.length){
            id = 0;
        }
        clearInterval(interval);
        return reviewItem.img;
    }

    function updateText(){
        var interval = setInterval(function(){ 
            updateReviewItem(ReviewsContent[id++]); 
        }, 3000);
        if(id == ReviewsContent.length){
            id = 0;
        }
        clearInterval(interval);
        return reviewItem.text;
    }

    return (
        <div>
            <Navbar />
            <Hero />
            <Intro />
            <Categories />
            <Decoration />
            <BestSellers />
            <Reviews img = {updateImage()} text = {updateText()}/>
            <Offers />
            <Footer />
        </div>
    );
}

export default App;
