import React, {useState} from 'react';

function ReviewCard(props){

    const [fade, updateFade] = useState(" ");

    function addFadeClass(){
        updateFade();

        return fade;
    }

    return (
        <div className = "reviewCard">
            <img className = {addFadeClass() + " imgCrop"} src = {props.img} width= "200" height = "200"/>
            <h3 className = "textPosition">{props.text}</h3>
        </div>
    );
}

export default ReviewCard;
